import { useState } from "react";
import Expenses from "./components/Expenses/Expenses";
import NewExpense from "./components/NewExpense/NewExpense";

const dummyExpenses = [
  { 
    id: 'e1', 
    title: 'Toilet Paper', amount: 94.12, date: new Date(2020, 7, 14) },
  { 
    id: 'e2', 
    title: 'New TV', amount: 799.49, date: new Date(2021, 0, 12) },
  { 
    id: 'e3', 
    title: 'Car Insurance', amount: 294.67, date: new Date(2021, 2, 28) },
  { 
    id: 'e4', 
    title: 'New Desk (Wooden)', amount: 449.99, date: new Date(2021, 5, 2) },
  { 
    id: 'e5', 
    title: 'Laptop Maintainance', amount: 699.99, date: new Date(2021, 12, 12) },
  { 
    id: 'e6', 
    title: 'Car', amount: 2500, date: new Date(2017, 12, 12) },
  { 
    id: 'e7', 
    title: 'Book', amount: 69.99, date: new Date(2022, 12, 12) },
  { 
    id: 'e8', 
    title: 'House rent', amount: 99.99, date: new Date(2019, 12, 12) },
  { 
    id: 'e9', 
    title: 'Laptop', amount: 109.99, date: new Date(2020, 12, 12) },
  { 
    id: 'e10', 
    title: 'Elec Bill', amount: 38.99, date: new Date(2019, 12, 12) },
];


function App() {
  const [expenses, setExpenses] = useState(dummyExpenses);
  const addExpenseHandler = expense => (
    setExpenses(prevExpenses => {
      return [expense, ...prevExpenses];
    })
  )


  return (
    <div>
      <NewExpense onAddExpense={addExpenseHandler} />
      <Expenses items={expenses} />
    </div>
  );
}

export default App;
